{
          "description": "A Sample Activity containing a single shared writing bric",
          "targetSelectorValues": [ "SWQ1-cntr" ],

          "sequenceNodeKey": "b3d5b510-34dd-480f-b8e1-b474522c9b03",
          "maxAttempts": 3,

          "containerConfig":
          [
              {
                  "description": "container for the shared writing question",
                  "containerId": "SWQ1-cntr",
                  "brixConfig":
                  [
                      {
                          "bricId": "swq1",
                          "bricType": "SharedWritingQuestion",
                          "config":
                          {
                              "id": "swq-00001",
