                              }
                          }
                      }
                  ],
                  "mortarConfig":
                  [
                      {
                          "mortarId": "interaction",
                          "mortarType": "InteractionLifecycleEvent",
                          "config":
                          {
                              "id": "swInteraction",
                          },
                          "configFixup":
                          [
                              {
                                  "type": "set-property",
                                  "name": "topic",
