                                  {
                                      "type": "brix-topic",
                                      "bricType": "SharedWritingQuestion",
                                      "instanceId": "swq-00001",
                                      "eventName": "value-changed"
                                  }
                              },
                              {
                                  "type": "set-property",
                                  "name": "seqNodeKey",
                                  "value": { "type": "ref", "domain": "info", "refId": "sequenceNodeKey" }
                              },
                              {
                                  "type": "set-property",
                                  "name": "requestManager",
                                  "value": { "type": "request-manager" }
                              }
                          ]
                      }
                  ],
                  "hookupActions":
                  [
                      {
                          "description": "Hookup the request manager to evaluate answers",
                          "type": "method-call",
                          "instance": { "type": "request-manager" },
                          "methodName": "handleRequestsFrom",
                          "args":
                          [
                              { "type": "ref", "domain": "brix", "refId": "swq1" }
                          ]
                      },
                      {
                          "description": "Hookup the request manager to handle requests for discussion posts",
                          "type": "method-call",
                          "instance": { "type": "request-manager" },
                          "methodName": "handleGetPostsRequestsFrom",
                          "args":
                          [
                              { "type": "ref", "domain": "brix", "refId": "swq1" }
                          ]
                      },
                      {
                          "description": "Hookup the request manager to handle requests for adding topic comments",
                          "type": "method-call",
                          "instance": { "type": "request-manager" },
                          "methodName": "handleAddTopicCommentRequestsFrom",
                          "args":
                          [
                              { "type": "ref", "domain": "brix", "refId": "swq1" }
                          ]
                      },
                      {
                          "description": "Draw the question",
                          "type": "method-call",
                          "instance": {"type": "ref", "domain": "brix", "refId": "swq1"},
                          "methodName": "draw",
                          "args":
                          [
                              { "type": "d3select", "targetSelectorIndex": 0 }
                          ]
                      }
                  ]
              }
          ]
      }

